package Interview;

public class TableMultiple {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		TableMultiple1(12);
	}
	
	public static void TableMultiple1(int a) {
		
		
		
		for(int i=1; i<=10;i++) {
			
			System.out.println(a + "x"+ i +":" +"=" + a*i);
		}
		
	}

}
